// Scan applies the selector query and scans the result into the given value.
func (mpps *MemberProductPropertySelect) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, mpps.ctx, "Select")
	if err := mpps.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*MemberProductPropertyQuery, *MemberProductPropertySelect](ctx, mpps.MemberProductPropertyQuery, mpps, mpps.inters, v)
}

func (mpps *MemberProductPropertySelect) sqlScan(ctx context.Context, root *MemberProductPropertyQuery, v any) error {
	selector := root.sqlQuery(ctx)
	aggregation := make([]string, 0, len(mpps.fns))
	for _, fn := range mpps.fns {
		aggregation = append(aggregation, fn(selector))
	}
	switch n := len(*mpps.selector.flds); {
	case n == 0 && len(aggregation) > 0:
		selector.Select(aggregation...)
	case n != 0 && len(aggregation) > 0:
		selector.AppendSelect(aggregation...)
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := mpps.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}
